// Helper to get division by id
function getDivision(divId) {
    return BD_DATA.divisions.find(d => d.id === divId);
}

// Helper to get district by id
function getDistrict(distId) {
    for (const div of BD_DATA.divisions) {
        const dist = div.districts.find(d => d.id === distId);
        if (dist) return { ...dist, divisionId: div.id, divisionName: div.name };
    }
    return null;
}
